import React from "react" 
import "./modal.css"

const Modal = props => {
    const [show, setShow] = useState(false);
  const [contacts, setContacts] = useState(data);
  const [addFormData, setAddFormData] = useState({
    title: "",
    textContent: "",
    currentDate: "",

  });


  const [editFormData, setEditFormData] = useState({
    title: "",
    textContent: "",
    currentDate: "",

  });



  const [editContactId, setEditContactId] = useState(null);

  const handleAddFormChange = (event) => {
    event.preventDefault();

    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;

    const newFormData = { ...addFormData };
    newFormData[fieldName] = fieldValue;

    setAddFormData(newFormData);




  };

  const handleEditFormChange = (event) => {
    event.preventDefault();

    const fieldName = event.target.getAttribute("name");
    const fieldValue = event.target.value;

    const newFormData = { ...editFormData };
    newFormData[fieldName] = fieldValue;

    setEditFormData(newFormData);
  };

  const handleAddFormSubmit = (event) => {
    event.preventDefault();

    const newContact = {
      id: nanoid(),
      title: addFormData.title,
      textContent: addFormData.textContent,
      currentDate: addFormData.currentDate,

    };

    const newContacts = [...contacts, newContact];
    setContacts(newContacts);

    const firstInput = document.getElementsByClassName('firstInput');
    console.log(firstInput)
    firstInput.value = '';
  };

  const handleEditFormSubmit = (event) => {
    event.preventDefault();

    const editedContact = {
      id: editContactId,
      title: editFormData.title,
      textContent: editFormData.textContent,
      currentDate: editFormData.currentDate,

    };

    const newContacts = [...contacts];

    const index = contacts.findIndex((contact) => contact.id === editContactId);

    newContacts[index] = editedContact;

    setContacts(newContacts);
    setEditContactId(null);


  };

  const handleEditClick = (event, contact) => {
    event.preventDefault();
    setEditContactId(contact.id);

    const formValues = {
      title: contact.title,
      textContent: contact.textContent,
      currentDate: contact.currentDate,

    };

    setEditFormData(formValues);
  };

  const handleCancelClick = () => {
    setEditContactId(null);
  };

  const handleDeleteClick = (contactId) => {
    const newContacts = [...contacts];

    const index = contacts.findIndex((contact) => contact.id === contactId);

    newContacts.splice(index, 1);

    setContacts(newContacts);
  };

    if (!props.show) {
        return null
    }
    return (
        <div className="modal">
            <div className="modal">
            <div className="modal-content">
            <div className="modal-header">
                <h4 className="modal-title">Modal-title</h4>
            </div>
            <div className="modal-body">
            <form  className="firstInput" onSubmit={handleAddFormSubmit}>
        <input 
          type="text"
          name="title"
          required="required"
          minlength = "3"
          maxLength= "250"
          placeholder="Tytuł"
          onChange={handleAddFormChange}
        />
        <input
          type="text"
          name="textContent"
          required="required"
          placeholder="Wiadomość"
          onChange={handleAddFormChange}
        />




      </form>
            </div>
            <div className="modal-footer">
                <button onClick={props.onClose} className="button">Close</button>
                <button className="button">Dodaj</button>
            </div>
            </div>
            </div>
        </div>
    )
}

export default Modal
